import { IPlatform, ICreator, IObject, ILeaf, IEvent } from '@leafer/interface';

declare const Platform: IPlatform;

declare const Creator: ICreator;

declare const UICreator: {
    list: IObject;
    register(UI: IObject): void;
    get(tag: string, data?: IObject, x?: number, y?: number, width?: number, height?: number): ILeaf;
};

declare const EventCreator: {
    nameList: IObject;
    register(Event: IObject): void;
    changeName(oldName: string, newName: string): void;
    has(type: string): boolean;
    get(type: string, ...params: unknown[]): IEvent;
};

export { Creator, EventCreator, Platform, UICreator };
